import axios from 'axios';


const url = 'https://covid19.mathdro.id/api';
export const fetchData = async()=>{
    try {
    const {data}= await axios.get(url);
    const modifiedData={
        confirmed: data.confirmed,
        recovered:data.recovered,
        deaths:data.deaths,
        lastUpdata:data.lastUpdata
    }
    return modifiedData;

    } catch (error) {

    }

}
